import React from "react"
import Plate from "./components/Plate"

const max = 15;
const plateStateArr = Array.from(Array(max), () => Array(max).fill(0))


class App extends React.Component{

  constructor(props) {
    super(props);
    this.state = {
      plateState: plateStateArr,
      turn: 1, // turn 1 은 흑돌, turn -1 은 백돌
      stonrState: 0,
      stoneColor: ""
    }
    this.turnChange=this.turnChange.bind(this)
  }

  turnChange() {
    if(this.state.stoneState === 0){
      if(this.state.turn === 1){
        this.setState({
          turn: -1
        })
      }else if(this.state.turn === -1){
        this.setState({
          turn:1
        })
      }
    }else{
      return;
    }
    console.log("test");
    console.log(this.state.turn);
  }  

  render(){
    return (
      <div>
        <Plate change={this.turnChange} turn={this.state.turn} stoneState={this.state.stoneState}/>
      </div>
    );
  }
}


export default App;
