import React from "react"
import Plate from "./components/Plate"

const max = 15;
const plateStateArr = Array.from(Array(max), () => Array(max).fill(0))


class App extends React.Component{

  constructor(props) {
    super(props);
    this.state = {
      plateState: plateStateArr,
      turn: 0, // turn 0 은 흑돌, turn 1 은 백돌
      stone: "⚫️"
    }
    this.turnChange=this.turnChange.bind(this)
  }

  turnChange() {
    if(this.state.turn === 0){
      this.setState({
        turn:1,
        stone:"⚪️"
      })
    }else{
      this.setState({
        turn:0,
        stone:"⚫️"
      })
    }
    console.log("test");
    console.log(this.state.turn);
  }  

  render(){
    return (
      <div>
        <Plate change={this.turnChange} stoneState={this.state.stone}/>
      </div>
    );
  }
}


export default App;
