const copyArr = require('../rules/arrayCopy')

exports.prohibit3by3 = (plateArr, lineNum) => {
    let tmpArr;
    tmpArr = copyArr.arrayCopy(plateArr, tmpArr, lineNum);
    let check3By3Arr = [];
    for (let i = 2; i < lineNum-2; i++) {
        for (let j = 2; j < lineNum-2; j++) {
            if( tmpArr[i][j]===1 && 
                tmpArr[i-1][j]===1 &&
                tmpArr[i][j+1]===1 &&
                tmpArr[i+1][j]===1 &&
                tmpArr[i][j-1]===1 &&
                tmpArr[i-2][j]===0 &&
                tmpArr[i][j+2]===0 &&
                tmpArr[i+2][j]===0 &&
                tmpArr[i][j-2]===0 ){
                    return "prohibitRule"
            }
        }
    }
    check3By3Arr[0]=tmpArr[i][j]+tmpArr[i-1][j]+tmpArr[i-2][j]+tmpArr[i-3][j]
    check3By3Arr[1]=tmpArr[i][j]+tmpArr[i-1][j+1]+tmpArr[i-2][j+2]+tmpArr[i-3][j+3]
    check3By3Arr[2]=tmpArr[i][j]+tmpArr[i][j+1]+tmpArr[i][j+2]+tmpArr[i][j+3]
    check3By3Arr[3]=tmpArr[i][j]+tmpArr[i+1][j+1]+tmpArr[i+2][j+2]+tmpArr[i+3][j+3]
    check3By3Arr[4]=tmpArr[i][j]+tmpArr[i+1][j]+tmpArr[i+2][j]+tmpArr[i+3][j]
    check3By3Arr[5]=tmpArr[i][j]+tmpArr[i+1][j-1]+tmpArr[i+2][j-2]+tmpArr[i+3][j-3]
    check3By3Arr[6]=tmpArr[i][j]+tmpArr[i][j-1]+tmpArr[i][j-2]+tmpArr[i][j-3]
    check3By3Arr[7]=tmpArr[i][j]+tmpArr[i-1][j-1]+tmpArr[i-2][j-2]+tmpArr[i-3][j-3]
}
